$("[data-gaev]").each(function() {
    var vals = $(this).data('gaev');
    $(this).click(function() {
        if (typeof vals.c === "undefined" || typeof vals.a === "undefined" || typeof vals.l === "undefined") return false;
        if (typeof vals.e === "undefined") vals.e = undefined;
        GTMpush(vals.c, vals.a, vals.l, vals.e);
    });
});

function GTMpush(category, action, label, event) {
    if (typeof category === "undefined" || typeof action === "undefined" || typeof label === "undefined") return false;
    if (typeof event === "undefined") event = "gaEvent";
    if (!dataLayer) return false;
    
    var eventObj = {
        'event': event,
        'eventCategory': category,
        'eventAction': action,
        'eventLabel': label
    };
    dataLayer.push(eventObj);
}
//  DISPLAY A 'STICKY' NAVIGATION
function fixNavigationDesktop(e) {
  if (e > 1015) {
    if (window.scrollY > 0) {
      document.getElementById('darker').style.backgroundColor = '#2F353E'
      document.getElementById('darker').style.position = 'fixed'
    } else {
      document.getElementById('darker').style.backgroundColor = '#2F353E'
      document.getElementById('darker').style.position = 'relative'
    }
  } else {
    $('#darker').removeAttr('style')
  }
}

//  HANDLE TRANSPARENT NAV

var header_anchor = $('.header-anchor')
if (header_anchor.length > 0 && $('body').hasClass('headscroll')) {
  $(window).scroll(function() {
    var win_pos = document.documentElement.scrollTop
      ? document.documentElement.scrollTop
      : document.body.scrollTop
    var anchor_pos = header_anchor.offset().top + header_anchor.outerHeight()
    //console.log(win_pos+" >= "+anchor_pos);
    if (win_pos >= anchor_pos && $('body').hasClass('transparent')) {
      // Slide in sticky nav
      //console.log(win_pos+" >= "+anchor_pos+" | Slide in");
      $('header')
        .css({ top: '-62px' })
        .animate({ top: '0' }, 300, function() {
          $(this).removeAttr('style')
        })
      $('body')
        .removeClass('transparent')
        .css('padding-top', '0px')
    } else if (
      win_pos < anchor_pos &&
      !$('body').hasClass('transparent') &&
      !$('body').hasClass('temptransparent')
    ) {
      // Slide out sticky nav
      //console.log(win_pos+" >= "+anchor_pos+" | Slide out");
      $('body').addClass('temptransparent')
      $('header')
        .css({ top: '0' })
        .animate({ top: '-62px' }, 100, function() {
          $(this).removeAttr('style')
          $('body')
            .addClass('transparent')
            .removeClass('temptransparent')
            .removeAttr('style')
        })
    }
  })
}

//  SHOW THE MOBILE NAVIGATION
function toggleMobileNav() {
  var html = $('html')
  var body = $('body')
  var menu = $('#mainNav')
  var menulink = $('.menu-link')
  var winHeight = $(window).height()
  var setHeight

  menulink.click(function() {
    winHeight = $(window).height()
    menulink.toggleClass('active')
    menulink.parent().toggleClass('active')
    html.toggleClass('noscroll')
    if (menu.hasClass('active')) {
      menu.removeClass('active', 400).removeAttr('style')
    } else {
      setHeight = winHeight - 62
      menu.css('height', setHeight).addClass('active', 400)
    }

    return false
  })

  $(window).resize(function() {
    if (menu.hasClass('active')) {
      winHeight = $(window).height()
      setHeight = winHeight - 62
      menu[0].style.height = setHeight + 'px'
    }
  })

  $('#mainNav').removeAttr('style')
}

// SHOW MOBILE SIGN UP BUTTON
$(window).scroll(function() {
  var banner = $('#mobileBanner');
  var cmLogo = $('#cmlogo');
  var isScrolled = window.scrollY > 10;

  if(isScrolled) {
    cmLogo.addClass('logo-small');
    banner.addClass('visible');
  } else {
    banner.removeClass('visible');
    cmLogo.removeClass('logo-small');
  }

})

// SET ACTIVE CLASSES ON DROPDOWN MENUS
var mobileNav = $('#mainNav')

$('.dropdown').each(function() {
  $(this).click(function(event) {
    var width = $(window).width()
    if (width < 850) {
      if (event.target.id) {
        var menu = $(this).find('.dropdown_menu--' + event.target.id)
        var link = $('#' + event.target.id)
        menu.toggleClass('active')
        link.toggleClass('active')
      }
    }
  })
})

function hideOnClickOutside(selector) {
  function outsideClickListener(event) {
    $target = $(event.target)
    if (!$target.closest('.dropdown').length) {
      $('.dropdown_link').each(function(i) {
        if ($(this).hasClass('active')) {
          $(this).removeClass('active')
        }
      })
      $('.dropdown_menu').each(function(i) {
        if ($(this).hasClass('active')) {
          $(this).removeClass('active')
        }
      })
    }
  }

  function removeClickListener() {
    document.removeEventListener('click', outsideClickListener)
  }

  document.addEventListener('click', outsideClickListener)
}

hideOnClickOutside($('.dropdown'))

$(document).ready(function() {
  toggleMobileNav()

  $('body').addClass('js')
})

/*
 * Header menu subnav
 */

$('header ul#second .open-subnav').click(function() {
  if ($(this).next('.subnav').length > 0) {
    var winHeight = $(window).height()
    var gap = deviceDetectGap()
    winHeight = winHeight + gap
    $(this)
      .next('.subnav')
      .css('height', winHeight)
      .addClass('open')
      .animate({ opacity: 1 }, 300, function() {})
  }
})

$(document).on('click touchstart keyup', function(e) {
  if (
    (!$(e.target).hasClass('block') &&
      !$(e.target).closest('.block').length > 0 &&
      !$(e.target).hasClass('open-subnav')) ||
    e.keyCode == 27
  ) {
    e.stopPropagation()
    $('header ul#second .subnav').animate({ opacity: 0 }, 300, function() {
      $(this)
        .removeClass('open')
        .removeAttr('style')
      $('#overlay-display-sales')
        .show()
        .next('.sales-info')
        .hide()
        .parent()
        .removeAttr('style')
        .next()
        .removeAttr('style')
        .parent()
        .removeAttr('style')
    })
  }
})

$('#overlay-display-sales').click(function() {
  var btn = $(this)
  var sales_cont = $(this).parent()
  var sales_width = sales_cont.width()
  var support_cont = sales_cont.next()
  var block = sales_cont.parent()
  support_cont.fadeOut(200, function() {
    sales_cont.css({ width: sales_width })
    block.animate(
      { width: sales_width, 'margin-left': -(sales_width / 2) },
      300,
      function() {
        btn.hide()
        sales_cont.children('.sales-info').slideDown()
      }
    )
  })
})

$('.subtitle.call')
  .parent()
  .click(function(e) {
    if ($(e.target).hasClass('call')) {
      e.preventDefault()
      var href = $(this)
        .children('.call')
        .data('href')
      window.location = href
    }
  })

function deviceDetectGap() {
  // Assign a variable for the application being used
  var nVer = navigator.appVersion
  // Assign a variable for the device being used
  var nAgt = navigator.userAgent
  var nameOffset, verOffset, ix

  // First check to see if the platform is an iPhone or iPod
  if (
    navigator.platform == 'iPhone' ||
    navigator.platform == 'iPod' ||
    navigator.platform == 'iPad'
  ) {
    // In Safari, the true version is after "Safari"
    if ((verOffset = nAgt.indexOf('Safari')) != -1) {
      // Set a variable to use later
      var mobileSafari = 'Safari'
    }
  }

  // If is mobile Safari set window height +60
  if (mobileSafari == 'Safari') {
    return 60
  } else {
    return 0
  }
}

