$(document).ready(function() {
  var navLinks = $('.primary-nav-link')
  var dropdownMenus = $('.dropdown_menu')
  var subnav = $('.subnav')
  var contact = $('#contact')
  var ARROW_DOWN = 40
  var ARROW_UP = 38
  var ARROW_RIGHT = 39
  var ARROW_LEFT = 37
  var ENTER = 13
  var TAB = 9

  // Hide all open dropdown menus
  function hideDropdowns() {
    navLinks.each(function() {
      if ($(this).hasClass('dropdown_link')) {
        if (
          $(this)
            .parent()
            .siblings('.dropdown_menu')
            .hasClass('active')
        ) {
          $(this).toggleClass('active')
          $(this)
            .parent()
            .siblings('.dropdown_menu')
            .toggleClass('active')
          $(this).attr('aria-expanded', false)
        }
      }
    })
  }

  function handleDropdownClick(self) {
    // Only do work on desktop
    var width = $(window).width()
    if (width > 850) {
      hideDropdowns()

      // Set current active dropdown
      if ($(self).hasClass('dropdown_link')) {
        $(self).toggleClass('active')
        $(self)
          .parent()
          .siblings('.dropdown_menu')
          .toggleClass('active')
        $(self).attr('aria-expanded', true)

        // Move focus to first item in menu
        $(self)
          .parent()
          .siblings('.dropdown_menu')
          .find('.dropdown_menu-item:first-of-type > a')
          .focus()
      }
    }
  }

  function handleDropdownKeydown(event) {
    // Only do work on desktop
    var width = $(window).width()
    if (width > 850) {
      switch (event.keyCode) {
        case ARROW_DOWN:
        case ENTER:
          var self = this

          handleDropdownClick(self)

          // Show contact nav
          if ($(self).is('#contact')) {
            $(subnav).addClass('open')
            $(subnav).css('height', '100vh')
            $(subnav).css('opacity', '1')
            $('.sublink:first-of-type').focus()
          }

          break
        case ARROW_RIGHT:
          hideDropdowns()

          // Move focus to next nav link
          if ($(this).hasClass('dropdown_link')) {
            // Handle dropdown links
            if ($(this).parents('.nav_item-secondary').length) {
              // Handle secondary nav
              $(this)
                .parent()
                .parent()
                .prev('.nav_item')
                .find('a')
                .focus()
            } else {
              // Handle primary nav
              $(this)
                .parent()
                .parent()
                .next()
                .find('a')
                .focus()
            }
          } else {
            // Handle main menu links
            if ($(this).parents('.nav_item-secondary').length) {
              // Handle secondary nav
              $(this)
                .parent()
                .prev('.nav_item')
                .find('a')
                .focus()
            } else {
              if (
                $(this)
                  .parent()
                  .next('.nav_item').length === 0
              ) {
                // Handle last item in primary nav
                $(this)
                  .parent()
                  .parent()
                  .siblings('.secondary-nav')
                  .find('.nav_item-secondary:last-of-type a')
                  .focus()
              } else {
                // Handle parary nav
                $(this)
                  .parent()
                  .next('.nav_item')
                  .find('a')
                  .focus()
              }
            }
          }
          break
        case ARROW_LEFT:
          hideDropdowns()

          // Move focus to previous nav link
          if ($(event.target).hasClass('dropdown_link')) {
            // Handle dropdown links
            if (
              $(this)
                .parent()
                .parent()
                .parent()
                .parent()
                .hasClass('secondary-nav')
            ) {
              // Handle secondary nav
              $(this)
                .parent()
                .parent()
                .next('.nav_item')
                .find('a')
                .focus()
            } else {
              console.log('running second')
              // Handle primary nav
              $(this)
                .parent()
                .parent()
                .prev('.nav_item')
                .find('a')
                .focus()
            }
          } else {
            // Handle main nav links
            if (
              $(this)
                .parent()
                .parent()
                .parent()
                .hasClass('secondary-nav')
            ) {
              // Handle secondary nav
              if (
                $(this)
                  .parent()
                  .next('.nav_item').length === 0
              ) {
                // Handle last item in secondary nav
                $(this)
                  .parent()
                  .parent()
                  .parent()
                  .siblings('.primary-nav')
                  .find('.nav_item a#agencies')
                  .focus()
              } else {
                $(this)
                  .parent()
                  .parent()
                  .next('.nav_item')
                  .find('a')
                  .focus()
              }
            } else {
              // Handle primary nav
              $(this)
                .parent()
                .prev('.nav_item')
                .find('a')
                .focus()
            }
          }
        case TAB:
          hideDropdowns()
      }
    }
  }

  function handleNavItemKeydown(event) {
    if (event.keyCode === ARROW_UP) {
      if ($(event.target).hasClass('dropdown_menu-link')) {
        // Move focus to main nav link if first item
        if (
          $(event.target)
            .parent('li')
            .prev('li').length == 0
        ) {
          $(event.target)
            .parent()
            .parent()
            .siblings('.dropdown_head')
            .find('a')
            .focus()
        } else {
          // Move focus to next nav item if not first item
          $(event.target)
            .parent()
            .siblings('.dropdown_menu-item')
            .find('a')
            .focus()
        }
      }
    }

    if (event.keyCode === ARROW_DOWN) {
      // Move focus to main nav link if last item
      if (
        $(event.target)
          .parent('li')
          .next('li').length == 0
      ) {
        $(event.target)
          .parent()
          .parent()
          .siblings('.dropdown_head')
          .find('a')
          .focus()
      } else {
        // Move focus to main nav link if not last item
        $(event.target)
          .parent()
          .siblings('.dropdown_menu-item')
          .find('a')
          .focus()
      }
    }
  }

  navLinks.each(function() {
    var self = this
    $(this).on('click', function() {
      handleDropdownClick(self)
    })

    $(this).on('keydown', handleDropdownKeydown)
  })

  dropdownMenus.each(function() {
    $(this).on('keydown', handleNavItemKeydown)
  })

  contact.on('click', function() {
    $('.sublink:first-of-type').focus()
  })
})


