/*
*  localStorage/sessionStorage function with fallback option to cookie if not supported
*      method: (String) Options:get|set|remove. Default: get
*      key: (String) key name for stored value
*      value: (String) value to be stored
*      expire: (Boolean) Default:false. If true, will use session length
*/

if (typeof cmStorage !== "function") {
  function cmStorage(method, key, value, expire) {
      var methodOptions = ['get', 'set', 'remove'];
      var methods = {
          'get':      'getItem',
          'set':      'setItem',
          'remove':   'removeItem'
      };
      if (typeof(method) === "undefined" || methodOptions.indexOf(method) === -1) method = 'get';
      if (typeof(expire) === "undefined") expire = false;
      if (typeof(Storage) !== "undefined") {
          if (expire) {
              return sessionStorage[methods[method]](key, value);
          } else {
              return localStorage[methods[method]](key, value);
          }
      } else {
          // Fall back to cookie suuport
          methods = {
              'get':      'get_cookie',
              'set':      'set_cookie',
              'remove':   'delete_cookie'
          };

          if (expire) {
              return window[methods[method]](key, value, 1);
          } else {
              return window[methods[method]](key, value);
          }
      }
  }
}

