/*
* AJAX Subscribe for the newsletter
*/

$(function () {
  $("#subForm input[type=email]").focus(function () {
      $(this).attr('placeholder', '');
      $("#subForm span.label").animate({top: '-10px', opacity: '1'}, 200);
      $("#subForm .subscribe").addClass('active');
  });

  $("#subForm input[type=email]").blur(function () {
      if ($(this).val() == '') {
          var input = $(this);
          $("#subForm span.label").animate({top: '0px', opacity: '0'}, 150, function () {
              input.attr('placeholder', 'Join our newsletter for email marketing tips');
          });
          $("#subForm .subscribe").removeClass('active');

      }
  });

  $("#subForm input[type=email]").keyup(function () {
      $("#subForm .label").removeClass("error");
      $("#subForm input[type=email]").removeClass("error");
  });

  $("#subForm .cmbtn").not('.disabled').click(function () {
      $('#subForm').submit();
  });

  // Not using cmForm library here due to its uniqueness
  $('#subForm').submit(function (e) {
      e.preventDefault();
      var btn = $("#subForm .cmbtn");
      var form = $(this);
      if (!btn.hasClass('disabled') && $("#subForm input[type=email]").val() !== '') {
          var errors = false;
          var fieldsArray = $('#subForm').serializeArray();
          var fields = {};
          $.each(fieldsArray, function (i, field) {
              var name = field.name;
              var value = field.value;

              if (name === 'cm-vuullj-vuullj' && !isValidEmailAddress(value)) {
                  errors = true;
              }

              if (name == 'check-alive' && value != '') {
                  errors = true;
              }

              fields[field.name] = field.value;
          });
          if (errors) {
              setError(form, 'cm-vuullj-vuullj', 'Please enter a valid email address');
              return false;
          }
          if (typeof (fields['check-alive']) !== 'undefined') {
              if (fields['check-alive'] !== '')
                  return false;
          }

          btn.addClass('disabled');
          fields['CS_account'] = "campmon";
          fields['CS_listID'] = "ba019aa688c3c9df26e2d2d092609051";
          fields['CS_fieldMap'] = {"name": "cm-name", "email": "cm-vuullj-vuullj"};
          fields['cm-name'] = '';
          $.post(this.action, fields, function (response) {
              btn.removeClass('disabled');
              if (!response) {
                  // Error
                  setError(form, 'cm-vuullj-vuullj', 'Something went wrong, please try again');
                  return false;
              } else {
                  // Success
                  $("#subForm input[type=email]").val('').blur();
                  $("#subResponse").addClass("show").text("Thanks. You're all set to receive our newsletter.");
              }
          });
      } else {
          setError(form, 'cm-vuullj-vuullj', 'Please enter a valid email address');
          return false;
      }
  });
});

function setError(form, field, msg) {
  if (form.find("[name=" + field + "]").length > 0) {
      form.find("[name=" + field + "]").next('.error_field').remove();
      form.find("[name=" + field + "]").addClass('error').after("<span class='error_field'>" + msg + "</span>");
      form.find("[name=" + field + "]").focus(function () {
          $(this).removeClass('error').next('.error_field').fadeOut(300, function () {
              $(this).remove();
          });
      });
  }
}

function isValidEmailAddress(emailAddress) {
  var pattern = /^([a-z\d!#$%&'*+\-\/=?^_`{|}~\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF]+(\.[a-z\d!#$%&'*+\-\/=?^_`{|}~\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF]+)*|"((([ \t]*\r\n)?[ \t]+)?([\x01-\x08\x0b\x0c\x0e-\x1f\x7f\x21\x23-\x5b\x5d-\x7e\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF]|\\[\x01-\x09\x0b\x0c\x0d-\x7f\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF]))*(([ \t]*\r\n)?[ \t]+)?")@(([a-z\d\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF]|[a-z\d\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF][a-z\d\-._~\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF]*[a-z\d\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])\.)+([a-z\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF][a-z\d\-._~\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF]*[a-z\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])\.?$/i;
  return pattern.test(emailAddress);
}

