$(function () {
  // Determine location
  setExistingLocale();
  if (!window['locale'].region || !window['locale'].countryName || !window['locale'].timezone || !window['locale'].stateName || !window['locale'].cityName || !window['locale'].zip || !window['locale'].countryISO) {
    determineLocation(storePricingData);
  } else {
    setLocationCookies();
    storePricingData();
  }

});

function setExistingLocale(whichRegion) {
  window['locale'] = {
      countryName:    get_cookie("CM-location-country"),
      countryISO:     get_cookie("CM-location-abbr"),
      timezone:       get_cookie("CM-location-timezone"),
      cityName:       get_cookie("CM-location-city"),
      stateName:      get_cookie("CM-location-state"),
      zip:            get_cookie("CM-location-zip"),
      euResident:     get_cookie("CM-location-eu"),
      status:         get_cookie("CM-location-status"),
  };

  if (typeof(whichRegion) !== "undefined" && whichRegion === true)
      window['locale'].region = get_cookie("CM-location-set");
  else
      window['locale'].region = get_cookie("CM-location-check");
}

function determineLocation(pricingDataCallback) {
  window['locale'] = {};

  var onSuccess = function (geoipResponse) {
      // console.log("determineLocation - geoip:", geoipResponse);
      // Continent Name
      if ("continent" in geoipResponse)
          window['locale'].continentName = geoipResponse.continent;
      // Region
      if ("region" in geoipResponse)
          window['locale'].region = geoipResponse.region;
      // Country Name
      if ("country" in geoipResponse)
          window['locale'].countryName = geoipResponse.country;
      // Country ISO
      if ("countryISO" in geoipResponse)
          window['locale'].countryISO = geoipResponse.countryISO;
      // Timezone
      if ("timezone" in geoipResponse)
          window['locale'].timezone = geoipResponse.timezone;
      // City Name
      if ("city" in geoipResponse)
          window['locale'].cityName = geoipResponse.city;
      // State Name
      if ("state" in geoipResponse)
          window['locale'].stateName = geoipResponse.state;
      // Zip/Postcode
      if ("zip" in geoipResponse)
          window['locale'].zip = geoipResponse.zip;
      // Is EU Resident
      if ("isEUresident" in geoipResponse)
          window['locale'].euResident = geoipResponse.isEUresident;
      // Locale set
      if ("status" in geoipResponse)
          window['locale'].status = geoipResponse.status;

      // Set cookies for future pageloads
      setLocationCookies();

      if (pricingDataCallback) {
        pricingDataCallback();
      }
  };

  var onError = function (error) {
      // console.log(error);
      // Continent Name
      window['locale'].continentName = 'Europe';
      // Region
      window['locale'].region = 'unitedkingdom';
      // Country Name
      window['locale'].countryName = 'United Kingdom';
      // Country ISO
      window['locale'].countryISO = 'UK';
      // Timezone
      //window['locale'].timezone = 'America/Los Angeles';
      // City Name
      //window['locale'].cityName = 'Los Angeles';
      // State Name
      //window['locale'].stateName = 'California';
      // Zip/Postcode
      //window['locale'].zip = '90001';
      // Is EU Resident
      window['locale'].euResident = true;
      // Locale set
      window['locale'].status = false;

      // Set cookies for future pageloads
      setLocationCookies();
  }

  geoLocate(onSuccess, onError);
}

function setLocationCookies(show_note) {
  if (typeof(show_note) === "undefined") show_note = true;
  var check_region = window['locale'].region;
  if (get_cookie("CM-location-check") && get_cookie("CM-location-set") && window['locale'].region !== get_cookie("CM-location-set")) {
      // User has changed regions AND not responded to the cookie notification. force check
      check_region = get_cookie("CM-location-set");

  } else if (!get_cookie("CM-location-check") && get_cookie("CM-location-set") && window['locale'].region == get_cookie("CM-location-set")) {
      if (window['consent_cookie_name'] && get_cookie(window['consent_cookie_name']) === null && display_regions.indexOf(window['locale'].region) > -1) {
          // EU users first visit since GDPR code implemented
          //delete_cookie(); // on and after May 25th

          // TEMPORARY :: Before May 25th - provide historical accept/deny notice

      } // Not in the EU just sets the cookie and moves on

      set_cookie("CM-location-check", window['locale'].region, 7);

  } else if (!get_cookie("CM-location-check") && get_cookie("CM-location-set") && window['locale'].region !== get_cookie("CM-location-set")) {
      // User has changed regions
      if (display_regions.indexOf(window['locale'].region) == -1) {
          // Previously EU
          check_region = get_cookie("CM-location-set");
          set_cookie("CM-location-check", get_cookie("CM-location-set"), 7);
      } else {
          set_cookie("CM-location-check", window['locale'].region, 0.04); // Set for under an hour
      }

  } else if (!get_cookie("CM-location-check") && "region" in window['locale']) set_cookie("CM-location-check", window['locale'].region, 7);

  if (!get_cookie("CM-location-set") && "region" in window['locale']) set_cookie("CM-location-set", window['locale'].region);
  if (!get_cookie("CM-location-country") && "countryName" in window['locale']) set_cookie("CM-location-country", window['locale'].countryName);
  if (!get_cookie("CM-location-abbr") && "countryISO" in window['locale']) set_cookie("CM-location-abbr", window['locale'].countryISO);
  if (!get_cookie("CM-location-timezone") && "timezone" in window['locale']) set_cookie("CM-location-timezone", window['locale'].timezone);
  if (!get_cookie("CM-location-city") && "cityName" in window['locale']) set_cookie("CM-location-city", window['locale'].cityName);
  if (!get_cookie("CM-location-state") && "stateName" in window['locale']) set_cookie("CM-location-state", window['locale'].stateName);
  if (!get_cookie("CM-location-zip") && "zip" in window['locale']) set_cookie("CM-location-zip", window['locale'].zip);
  if (!get_cookie("CM-location-eu") && "euResident" in window['locale']) set_cookie("CM-location-eu", window['locale'].euResident);
  if (!get_cookie("CM-location-status") && "status" in window['locale']) set_cookie("CM-location-status", window['locale'].status);

  var consent = true;
  if (display_regions.indexOf(check_region) === -1) consent = false;
  if (!get_cookie("CM-location-gdpr") || (get_cookie("CM-location-gdpr") == 'true' ? true : false) !== consent) {
      set_cookie("CM-location-gdpr", consent);
  }

  // Populate any country form fields
  updateForms(window['locale'].countryName, window['locale'].timezone, window['locale'].stateName, window['locale'].cityName, window['locale'].zip);
  setPhonenumber(window['locale'].region);

  // PARSE country to segment
  if (typeof (analytics) !== "undefined") {
      if (window['locale'].status === true) {
          analytics.identify({
              country: window['locale'].countryName,
              timezone: window['locale'].timezone,
              city: window['locale'].cityName,
              state: window['locale'].stateName,
              zip: window['locale'].zip
          });
      } else if (window['locale'].status === false) {
          analytics.identify({
              country: "notfound"
          });
      }
  }

  // Show cookie notification if needed
  if (show_note)
      displayCookieNote(check_region);
}

function geoLocate(onSuccess, onError) {
  var formsGeo = '/marketing-includes/forms/geo/';
  var href = window.location.href;

  if (href.indexOf('https://help.c') === -1 && href.indexOf('force.com') === -1) {
    $.getJSON(formsGeo, function(locale) {
      // console.log(locale);
      onSuccess(locale);
    }).fail(onError);
  }
}

