function updateConsent(consent, show_note) {
  if (typeof(show_note) === "undefined") show_note = true;
  if (!consent) {
      setExistingLocale(true);
      setLocationCookies(show_note);
      delete_cookie();
  }
  cookie_consent.given = consent;
  if (window['consent_cookie_name'])
      set_cookie(window['consent_cookie_name'], consent);
}

