function rotate(target) {
    var wrapper = $(".cm-customer-hero-wrap.slideshow");
    var handles = wrapper.children('.handles');
    
    var current = wrapper.find(".cm-customer-hero.current");
    if (typeof(target) !== 'undefined') {
        var next = $("#"+target);
    } else {
        var next = current.next('.cm-customer-hero');
        if (next.length === 0) next = wrapper.children(".cm-customer-hero").first();
    }
    
    current.removeClass('current');
    handles.find('.current').removeClass('current');
    next.addClass('current');
    handles.find('.'+next.attr('id')).addClass('current');
    if (typeof(target) === 'undefined') manual = setTimeout(rotate, 6000);
}

if ($(".cm-customer-hero-wrap.carousel").length > 0) {
    var manual;
    
    /* Init slider */
    var curr = $(".cm-customer-hero-wrap.carousel .current");
    curr.next().addClass('next');
    curr.parent().find(".cm-customer-hero").last().addClass('prev');
    
    manual = setTimeout(slide, 6000);
    
    $(".cm-customer-hero-wrap.carousel .arrow").click(function() {
        clearTimeout(manual);
        var dir = $(this).data('dir');
        slide(dir);
    });
}

