function viewport_calc() {
	var e = window,
		a = 'inner';
	if (!('innerWidth' in window)) {
		a = 'client';
		e = document.documentElement || document.body;
	}
	return { width: e[a + 'Width'], height: e[a + 'Height'] };
}

function randomize_rotation(block) {
	var dim = viewport_calc();
	var delay = 6000;
	if (dim.width > 770) {
		var curr_tweet = block.children('.tweet.active');
		var next_tweet = null;
		if (curr_tweet.next().length > 0) {
			next_tweet = curr_tweet.next();
		} else {
			next_tweet = block.children('.tweet').first();
		}

		curr_tweet.animate({ opacity: 0 }, 1000, function () {
			$(this).removeClass('active').removeAttr('style');
		});
		next_tweet.css({ opacity: 0, position: 'absolute', display: 'block' });
		var width = next_tweet.outerWidth();
		next_tweet
			.css({ top: 0, left: '50%', 'margin-left': '-' + width / 2 + 'px' })
			.animate({ opacity: 1 }, 1000, function () {
				$(this).addClass('active').removeAttr('style');
			});

		//        if (curr_tweet.parent().next().length > 0) {
		//            block = curr_tweet.parent().next();
		//        } else {
		//            block = $('.testimonials .quote.one');
		//        }

		var min = 5000;
		var max = 10000;
		delay = Math.floor(Math.random() * (max - min)) + min;
	}

	setTimeout(function () {
		randomize_rotation(block);
	}, delay);
}

