// COUNTDOWN TIMER

class CountDownTimer {
	/*
		CONSTRUCTOR ARGS
		timerElement - DOM element
		endTime - javascript date object
	*/
	constructor(timerElement, endTime){
		this.endTime = endTime.getTime()
		this.standardButton = timerElement.querySelector('.countdownTimer__standard-button')
		this.revealButton = timerElement.querySelector('.countdownTimer__reveal-button')
		this.days_box = timerElement.querySelector('#countdownTimer__days')
		this.hours_box = timerElement.querySelector('#countdownTimer__hours')
		this.minutes_box = timerElement.querySelector('#countdownTimer__minutes')
		this.seconds_box = timerElement.querySelector('#countdownTimer__seconds')
		this.days;
		this.hours;
		this.minutes;
		this.seconds;
		this.counter;
		this.debug = false
		this.init()
		
	}

	init() {
		this.countDown()
		this.counter = setInterval(this.countDown.bind(this), 1000)
	}

	convertToUTC(dateObject){
		const dateObjectMillis = dateObject.getTime()
		const tzOffset = dateObject.getTimezoneOffset() * 60000
		return dateObjectMillis
	}

	countDown(){
		const current = this.convertToUTC(new Date())
		const total = this.endTime - current;
		this.seconds = Math.floor((total / 1000) % 60);
		this.minutes = Math.floor((total / 1000 / 60) % 60);
		this.hours = Math.floor((total / (1000 * 60 * 60)) % 24);
		this.days = Math.floor(total / (1000 * 60 * 60 * 24));
		if(total <= 0){
			clearInterval(this.counter)
			this.switchToEndState()
		} else {
			this.updateHTML()
		}
		if(this.debug){
			console.log("current date: " + new Date(current))
			const checkdate = this.convertToUTC(new Date()) 
			+ (this.days * (1000 * 60 * 60 * 24) )
			+ (this.hours * 1000 * 60 * 60 )
			+ (this.minutes * 1000 * 60)
			+ (this.seconds * 1000)
			+ 1
			console.log("calculated end date: " + new Date(checkdate))
			const tz = new Date(checkdate).getTimezoneOffset()
			const utcDate = new Date(checkdate).getTime() + (tz * 60000)
			console.log("calculated UTC end time: " + new Date(utcDate))

			this.debug = false
		}
	}

	updateHTML(){
		this.days_box.innerText = ('0' + this.days).slice(-2)
		this.hours_box.innerText = ('0' + this.hours).slice(-2)
		this.minutes_box.innerText = ('0' + this.minutes).slice(-2)
		this.seconds_box.innerText = ('0' + this.seconds).slice(-2)
	}

	switchToEndState(){
		this.standardButton.style.display = 'none'
		this.revealButton.style.display = 'inline-block'
	}

}

