// FOR MIXPANEL

function waitUntilLoad(func) {
	if (mixpanel.__loaded) func();
	else
		setTimeout(function () {
			waitUntilLoad(func);
		}, 10);
}

function listAdd(property, value) {
	waitUntilLoad(function () {
		listAddLogic(property, value);
	});
}

function campaignParams() {
	waitUntilLoad(function () {
		campaignParamsLogic();
	});
}

function addIfUnique(list, value) {
	if (list.indexOf(value) == -1) list.push(value);
	return list;
}

function listAddLogic(property, value) {
	var obj = {};
	obj[property] = [];
	mixpanel.register_once(obj); //create an empty list property if it doesn't exist yet
	mixpanel.people.union(property, value);
	mixpanel.register(
		property,
		addIfUnique(mixpanel.get_property(property), value)
	);
}

function campaignParamsLogic() {
	var campaign_keywords = [
		'utm_source',
		'utm_medium',
		'utm_campaign',
		'utm_content',
		'utm_term',
	];

	var kw = '';
	var params = {};

	for (var i = 0; i < campaign_keywords.length; i++) {
		kw = getQueryParam(document.URL, campaign_keywords[i]);
		if (kw.length) {
			params[campaign_keywords[i] + ' [most recent]'] = kw;
			listAdd(campaign_keywords[i] + ' [list]', kw);
		}
	}

	mixpanel.register(params);
	mixpanel.people.set(params);
}

function getQueryParam(url, param) {
	param = param.replace(/[\[]/, '\\[').replace(/[\]]/, '\\]');
	var regexS = '[\\?&]' + param + '=([^&#]*)';
	var regex = new RegExp(regexS);
	var results = regex.exec(url);
	if (
		results === null ||
		(results && typeof results[1] !== 'string' && results[1].length)
	)
		return '';
	else return decodeURIComponent(results[1]).replace(/\+/g, ' ');
}

function ensureLoaded() {
	if (typeof mixpanel !== 'undefined') {
		mixpanel.init('1c1eda798f92601aecaa904fe7b3520a', {
			loaded: function () {
				campaignParams();
			},
		});
	} else {
		setTimeout(ensureLoaded, 1000);
	}
}

